    static func getRandomPlans(_ goal :String, completion:@escaping ([[Exercise]]?) -> ()) {
        let urlPath = "\(baseURL)ALL\(authKey)"
        let url = URL(string: urlPath)!
        
        do {
            var categories = try JSONDecoder().decode([Exercise].self, from: Data(str.utf8))
            
            var plans :[[Exercise]] = []
            
            for _ in 1...5 {
                var filteredCats :[Exercise] = []
                
                categories = categories.shuffled()
                
                var flag = false
                for act in categories {
                    if (act.category.hasPrefix(goal)) {
                        // Mandatory flexibility exercise
                        if (!flag && filteredCats.count == 3) {
                            continue
                        }
                        filteredCats.append(act)
                        if (filteredCats.count == 4) {
                            break
                        }
                    }
                    if (!flag && act.category.hasPrefix("F")) {
                        filteredCats.append(act)
                        flag = true
                        if (filteredCats.count == 4) {
                            break
                        }
                    }
                }
                plans.append(filteredCats)
            }
            
            DispatchQueue.main.async {
                completion(plans)
            }
        } catch {
            print(error)
        }
        
